import React, { useState } from "react";
import "./modal.css"
import {Modal, Button, Form} from "react-bootstrap"

const MyModal =({active, setActive})=>{
    
    const [dTitle, dImg, dDescription] = useState("");
    const AddHandler=()=>{
        console.log(dTitle, dImg, dDescription);
    }

        return(
            <div className={active?"modal active":"modal"} onClick={()=>setActive(false)}>
                <div className={active?"modal__content active":"modal__content"} onClick={e=>e.stopPropagation()}>
                
        <Modal.Header closeButton onClick={()=>setActive(false)}>
          <Modal.Title>Add custom dish</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form.Control type="text" placeholder="Dish title" className="mb-3" onChange={()=>dTitle(value)}></Form.Control>
            <Form.Control type="text" placeholder="Image url" className="mb-3" onChange={()=>dImg(this.value)}></Form.Control>
            <Form.Control as="textarea" rows={5} placeholder="Dish description..." className="mb-3" onChange={()=>dDescription(this.value)}></Form.Control>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={AddHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
                </div>
            </div>
        )
}

export default MyModal;
