import React, { useState } from "react";
import "./modal.css"
import {Modal, Button, Form} from "react-bootstrap"

const nDish = {
    idMeal:"",
    strMeal:"",
    strMealThumb:"",
    strInstructions:"",
};
const MyModal =({active, setActive})=>{
    const[strMeal,setMeal]=useState("");
    const[strImg,setImg]=useState("");
    const[strDescription,setDescription]=useState("");
    const[newDish,setNewDish]=useState(nDish);
    const AddHandler=()=>{
        //localStorage.setItem(localStorage.length,JSON.stringify(data));
        const num = localStorage.length.toString;
        const temp = nDish({num,strMeal,strImg,strDescription});
        setNewDish(num,strMeal,strImg,strDescription);
        console.log(temp);
        console.log("id: "+localStorage.length+"/strMeal: "+strMeal+"/strI: "+strImg+"/strDisc: "+strDescription);
        setMeal("");
        setImg("");
        setDescription("");
        setActive(false);
    }
    

        return(
            <div className={active?"modal active":"modal"} onClick={()=>setActive(false)}>
                <div className={active?"modal__content active":"modal__content"} onClick={e=>e.stopPropagation()}>
                
        <Modal.Header closeButton onClick={()=>setActive(false)}>
          <Modal.Title>Add custom dish</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form.Label>{strMeal}</Form.Label>
            <Form.Control type="text" id="title" placeholder="Dish title" name="strMeal" defaultValue={strMeal} className="mb-3" onChange={(e)=>setMeal(e.target.value)}></Form.Control>
            <Form.Control type="text" id="img" placeholder="Image url" name="strMealThumb" defaultValue={strImg} className="mb-3" onChange={(e)=>setImg(e.target.value)}></Form.Control>
            <Form.Control as="textarea" id="description" rows={5} placeholder="Dish description..." defaultValue={strDescription} onChange={(e)=>setDescription(e.target.value)} name="strInstructions" className="mb-3" ></Form.Control>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={AddHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
                </div>
            </div>
        )
}

export default MyModal;
