import React, {Component} from "react";
import { Card } from "react-bootstrap";

export default class Mmore extends Component{
    constructor(props){
        super(props);
        this.state = {
            items: []
        };
    }
    componentDidMount(){
        this.setState({
            items:this.props
        });
    }
    render(){
        const {items}=this.state;
        if(error){
            return <p>Error {error.message}</p>
        }else if(!isLoaded){
            return <p>Loading...</p>
        }else{
        return(
            
            items.forEach((i)=>{
                i.map(item=>(
                    <>
    <Card style={{ width: '90%', marginLeft:"20px", marginTop:"20px", padding:"20px", alignItems:"center" }}>
        <Card.Img variant="top" style={{width:'40%'}} src={item.strMealThumb} />
        <Card.Body>
            <Card.Title style={{textAlign:"center"}}>{item.strMeal}</Card.Title>
            <Card.Text>
                {item.strInstructions}
            </Card.Text>
        </Card.Body>
        
            
    </Card></>))
            })
            
            
        )
        
    }}
}
