import React, { Component } from "react";
import { Routes, Route } from "react-router";
import { More } from "./More";
import {Link} from 'react-router-dom';


export default class Rrecipe extends Component{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }

componentDidMount(){
    fetch("https://www.themealdb.com/api/json/v1/1/random.php")
    .then(res=>res.json())
    .then(
        (result)=>{
            this.setState({
                isLoaded: true,
                items:result.meals
            });
        },
        (error)=>{
            this.setState({
                isLoaded: true,
                error
            });
        }
    )
}

render(){
    const {error, isLoaded, items}=this.state;
    if(error){
        return <p>Error {error.message}</p>
    }else if(!isLoaded){
        return <p>Loading...</p>
    }else{
        return(
            <>
            <Routes>
                <Route path='/more' element={<More/>}/>
            </Routes>
            <Link to="/more">
            <div className="Container bg-light border" >
                {items.map(item=>(
                    <div key={items.name}>
                        <h3>
                        {item.strMeal}
                        </h3>
                        <img className="imgRecipe" src={item.strMealThumb} alt={item.strMeal}>
                        </img>
                        
                    </div>
                ))}
            </div>
            </Link>
            </>
        );
    }
}
}
