import React, { Component } from "react";

export default class Rrecipe extends Component{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }

componentDidMount(){
    fetch("https://www.themealdb.com/api/json/v1/1/random.php")
    .then(res=>res.json())
    .then(
        (result)=>{
            this.setState({
                isLoaded: true,
                items:result.meals
            });
        },
        (error)=>{
            this.setState({
                isLoaded: true,
                error
            });
        }
    )
}

render(){
    const {error, isLoaded, items}=this.state;
    if(error){
        return <p>Error {error.message}</p>
    }else if(!isLoaded){
        return <p>Loading...</p>
    }else{
        return(
            <ul>
                {items.map(item=>(
                    <li key={items.name}>
                        {item.strMeal}
                        <img src={item.strMealThumb} alt={item.strMeal}>
                        </img>
                    </li>
                ))}
            </ul>
        );
    }
}
}
