import React, {useState } from "react";
import "./modal.css"
import {Modal, Button, Form} from "react-bootstrap"
class InitialState extends Component{
    constructor(props){
        super(props);
        this.state = {
            idMeal:"",
            strMeal:"",
            strMealThumb:"",
            strInstructions:""
        };
    }
}

const MyModal =({active, setActive})=>{
    let strMeal ="";
    let strThumb="";
    let strInstructions="";
    let nDish = new InitialState();
    const AddHandler=()=>{
        if(document.getElementById("img").value===""){
            strThumb="https://img.freepik.com/free-vector/_212889-1967.jpg?size=626&ext=jpg";
        }
        
        nDish.idMeal = localStorage.length;
        nDish.strMeal = strMeal;
        nDish.strMealThumb = strThumb;
        nDish.strInstructions=strInstructions;
        console.log(nDish);
        let data = [];
        data.push(nDish);
        localStorage.setItem(localStorage.length,JSON.stringify(data));
        strMeal=strThumb=strInstructions="";
        setActive(false);
    }
    const handleChangeTitle=()=>{
        strMeal = document.getElementById("title").value;
    }
    const handleChangeImg=()=>{
        strThumb=document.getElementById("img").value;
    }
    const handleChangeDescr=()=>{
        strInstructions=document.getElementById("description").value;
    }
    

        return(
            <div className={active?"modal active":"modal"} onClick={()=>setActive(false)}>
                <div className={active?"modal__content active":"modal__content"} onClick={e=>e.stopPropagation()}>
                
        <Modal.Header closeButton onClick={()=>setActive(false)}>
          <Modal.Title>Add custom dish</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form.Control type="text" id="title" placeholder="Dish title" name="strMeal" defaultValue={strMeal} className="mb-3" onChange={handleChangeTitle}></Form.Control>
            <Form.Control type="text" id="img" placeholder="Image url" name="strMealThumb" defaultValue={strThumb} className="mb-3" onChange={handleChangeImg}></Form.Control>
            <Form.Control as="textarea" id="description" rows={5} placeholder="Dish description..." name="strInstructions" defaultValue={strInstructions} className="mb-3" onChange={handleChangeDescr}></Form.Control>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={AddHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
                </div>
            </div>
        )
}

export default MyModal;
