import React, { useState } from "react";
import "./modal.css"
import {Modal, Button, Form} from "react-bootstrap"

const nDish = {
    idMeal:"",
    strMeal:"",
    strMealThumb:"",
    strInstructions:"",
};
const MyModal =({active, setActive})=>{
    const[newDish,setNewDish]=useState(nDish);
    const AddHandler=()=>{
    
        setActive(false);
    }
    const onChange=(e)=>{
        const{name,value} = e.target;
        setNewDish({...newDish,[name]:value})
    }
    

        return(
            <div className={active?"modal active":"modal"} onClick={()=>setActive(false)}>
                <div className={active?"modal__content active":"modal__content"} onClick={e=>e.stopPropagation()}>
                
        <Modal.Header closeButton onClick={()=>setActive(false)}>
          <Modal.Title>Add custom dish</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form.Label>{strMeal}</Form.Label>
            <Form.Control type="text" id="title" placeholder="Dish title" name="strMeal" defaultValue={strMeal} className="mb-3" onChange={onChange}></Form.Control>
            <Form.Control type="text" id="img" placeholder="Image url" name="strMealThumb" defaultValue={strImg} className="mb-3" onChange={onChange}></Form.Control>
            <Form.Control as="textarea" id="description" rows={5} placeholder="Dish description..." defaultValue={strInstructions} onChange={onChange} name="strInstructions" className="mb-3" ></Form.Control>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={AddHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
                </div>
            </div>
        )
}

export default MyModal;
