import React, { useEffect, useState } from "react";
import "./modal.css"
import {Modal, Button, Form} from "react-bootstrap"
const InitialState={
    title:"",
    img:"",
    description:"",
};
const MyModal =({active, setActive})=>{
    const[title, setTitle] = useState("");
    const[img, setImg] = useState("");
    const[description, setDescription] = useState("");
    const[newDish, setNewDish] = useState(InitialState)
    const AddHandler=()=>{
        setNewDish(title, img, description);
        console.log(newDish);
    }
    const handleOnChange=(e)=>{
        if(e.target.attributes.name.value=='title')
        {
            setTitle(e.target.value);
        }else{
            if(e.target.attributes.name.value=='img')
            {
            setImg(e.target.value);
        }else{
            if(e.target.attributes.name.value=='description')
            {
                setDescription(e.target.value);
            }
        }

        }
        setTitle(e.target.value);
        console.log(e.target.attributes.name.value);
    }

        return(
            <div className={active?"modal active":"modal"} onClick={()=>setActive(false)}>
                <div className={active?"modal__content active":"modal__content"} onClick={e=>e.stopPropagation()}>
                
        <Modal.Header closeButton onClick={()=>setActive(false)}>
          <Modal.Title>Add custom dish</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form.Control type="text" placeholder="Dish title" name="title" defaultValue={title} className="mb-3" onChange={handleOnChange}></Form.Control>
            <Form.Control type="text" placeholder="Image url" name="img" defaultValue={img} className="mb-3" onChange={handleOnChange}></Form.Control>
            <Form.Control as="textarea" rows={5} placeholder="Dish description..." name="description" defaultValue={description} className="mb-3" onChange={handleOnChange}></Form.Control>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={AddHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
                </div>
            </div>
        )
}

export default MyModal;
