import React, {useState } from "react";
import "./modal.css"
import {Modal, Button, Form} from "react-bootstrap"
import { Route } from "react-router";
const InitialState={
    idMeal:"",
    strMeal:"",
    strMealThumb:"",
    strInstructions:"",
};
const MyModal =({active, setActive})=>{
    const[strMeal, setTitle] = useState("");
    const[strMealThumb, setImg] = useState("");
    const[strInstructions, setDescription] = useState("");
    const[newDish, setNewDish] = useState(InitialState)
    const AddHandler=()=>{
        if(strMealThumb===null){
            setImg("https://img.freepik.com/free-vector/_212889-1967.jpg?size=626&ext=jpg");
        }
        setNewDish({idMeal:localStorage.length, strMeal, strMealThumb, strInstructions});
        console.log(newDish);
        let data = [];
        data.push(newDish);
        localStorage.setItem(localStorage.length,JSON.stringify(data));
        setTitle("");
        setImg("");
        setDescription("");
    }
    const handleOnChange=(e)=>{
        if(e.target.attributes.name.value==='strMeal')
        {
            setTitle(e.target.value);
        }else{
            if(e.target.attributes.name.value==='strMealThumb')
            {
            setImg(e.target.value);
        }else{
            if(e.target.attributes.name.value==='strInstructions')
            {
                setDescription(e.target.value);
            }
        }

        }
    }

        return(
            <div className={active?"modal active":"modal"} onClick={()=>setActive(false)}>
                <div className={active?"modal__content active":"modal__content"} onClick={e=>e.stopPropagation()}>
                
        <Modal.Header closeButton onClick={()=>setActive(false)}>
          <Modal.Title>Add custom dish</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form.Control type="text" placeholder="Dish title" name="strMeal" defaultValue={strMeal} className="mb-3" onChange={handleOnChange}></Form.Control>
            <Form.Control type="text" placeholder="Image url" name="strMealThumb" defaultValue={strMealThumb} className="mb-3" onChange={handleOnChange}></Form.Control>
            <Form.Control as="textarea" rows={5} placeholder="Dish description..." name="strInstructions" defaultValue={strInstructions} className="mb-3" onChange={handleOnChange}></Form.Control>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={AddHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
                </div>
            </div>
        )
}

export default MyModal;
