import React, {Component} from "react";
import { Card } from "react-bootstrap";

export default class Mmore extends Component{
    constructor(props){
        super(props);
        this.state = {
            isLoaded: false,
            item: this.props.item
        };
    }
    componentDidMount(){
        if(this.props.item!=null){
            this.setState({
                isLoaded: true,
                item:this.props.item
            });
        }else{
                this.setState({
                    isLoaded: true
                });
            }
    }
    render(){
        const {item}=this.state;

            return(
                <>
                <div>
                    {
                        item.map(i=>(
                            <Card style={{ width: '90%', marginLeft:"20px", marginTop:"20px", padding:"20px", alignItems:"center" }}>
                            <Card.Img variant="top" style={{width:'40%'}} src={i.strMealThumb} />
                            <Card.Body>
                                <Card.Title style={{textAlign:"center"}}>{i.strMeal}</Card.Title>
                                <Card.Text>
                                {i.strInstructions}
                                </Card.Text>
                            </Card.Body>
                            </Card>
                        ))
                    }
                </div>
                </>
            )
    }
}
