import React, { Component } from "react";
import { Card } from "react-bootstrap";

export default class Favourites extends Component{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }
    componentDidMount()
    {
        let data =[]
        for(let i=0;i<localStorage.length;i++)
        {
            data.push(localStorage.getItem(i.toString()));
        }
        this.setState({
            isLoaded: true,
            items:data
        });
    }
    render(){
        const {error, isLoaded, items}=this.state;
        if(error){
            return <p>Error {error.message}</p>
        }else if(!isLoaded){
            return <p>Loading...</p>
        }else{
    return(
        <>
            {
                items.map(i=>(
                    <div className="favouriteCard">
                        <Card>
                    <Card.Img variant="top" src={i.strMealThumb} alt="dish" />
                    <Card.Body>
                        <Card.Title>{i.strMeal}</Card.Title>
                        <Card.Text>
                        {i.strInstructions}
                        </Card.Text>
                    </Card.Body>
                    </Card>
                    </div>
                ))
            }
        </>
    );
}
}


}
