import React, { useState } from "react";
import "./modal.css"
import {Modal, Button, Form} from "react-bootstrap"
const InitialState={
    title:"",
    img:"",
    description
};
const MyModal =({active, setActive})=>{
    
    const[newDish, setNewDish] = useState(InitialState)
    const AddHandler=()=>{
        console.log(title, img, description);
    }
    const handleOnChange=(e)=>{
        const{name,value}=e.target;
        setNewDish=({...newDish,[name]:value});
    }

        return(
            <div className={active?"modal active":"modal"} onClick={()=>setActive(false)}>
                <div className={active?"modal__content active":"modal__content"} onClick={e=>e.stopPropagation()}>
                
        <Modal.Header closeButton onClick={()=>setActive(false)}>
          <Modal.Title>Add custom dish</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form.Control type="text" placeholder="Dish title" defaultValue={title} className="mb-3" onChange={handleOnChange}></Form.Control>
            <Form.Control type="text" placeholder="Image url" className="mb-3" onChange={handleOnChange}></Form.Control>
            <Form.Control as="textarea" rows={5} placeholder="Dish description..." className="mb-3" onChange={handleOnChange}></Form.Control>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={AddHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
                </div>
            </div>
        )
}

export default MyModal;
