import React, { Component } from "react";
import { Modal } from "react-bootstrap";

export default class Rrecipe extends Component
{
    constructor(props){
        super(props);
        this.state = {
            show:false,
            setShow: false
        };
    }
    componentDidMount(){
        this.setState({
            show:false,
            setShow: this.props.setShow
        });
    }


    handleClose = () =>
    {
        this.setState({
            show:false,
            setShow: false
        });
    };

    render(){
        return(
            <Modal
            show={{show}}
            onHide={handleClose()}
            backdrop="static"
            keyboard={false}
          >
            <Modal.Header closeButton>
              <Modal.Title>Modal title</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <textarea defaultValue="input heare..."></textarea>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose()}>
                Close
              </Button>
              <Button variant="primary" onClick={handleOk(()=>{
                  console.log(textarea.text);
              })}>Understood</Button>
            </Modal.Footer>
          </Modal>
        )
    }
    
}
