import React from "react";
import { Card} from "react-bootstrap";

const Favorite = ()=>{
    let data=[]
    for(let i=0;i<localStorage.length;i++){
        data.push(JSON.parse(localStorage.getItem(localStorage.key(i))));
        console.log(data[i]);
        CreateRecipe(data[i]);
    }
    
}
CreateRecipe=(data)=>{
    return(
        data.map(item=>(
            <>
            <div>
            <Card style={{ width: '90%', marginLeft:"20px", marginTop:"20px", padding:"20px", alignItems:"center" }}>
                <Card.Img variant="top" style={{width:'40%'}} src={item.strMealThumb} />
                <Card.Body>
                    <Card.Title style={{textAlign:"center"}}>{item.strMeal}</Card.Title>
                    <Card.Text>
                        {item.strInstructions}
                    </Card.Text>
                </Card.Body>
                
                    
            </Card></div></>)))
}
export {Favorite}
