import React, { Component } from "react";
import { Card, Button, Row, Col } from "react-bootstrap";


export default class Rrecipe extends Component{
    constructor(props){
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }

componentDidMount(){
    fetch("https://www.themealdb.com/api/json/v1/1/random.php")
    .then(res=>res.json())
    .then(
        (result)=>{
            this.setState({
                isLoaded: true,
                items:result.meals
            });
        },
        (error)=>{
            this.setState({
                isLoaded: true,
                error
            });
        }
    )
}
render(){
    const {error, isLoaded, items}=this.state;
    if(error){
        return <p>Error {error.message}</p>
    }else if(!isLoaded){
        return <p>Loading...</p>
    }else{
        return(
            <>
            <div className="homePageContent">
                {items.map(item=>(
                <Card className="card">
                    <Card.Img variant="top" style={{width:'40%'}} src={item.strMealThumb} />
                    <Card.Body>
                        <Card.Title style={{textAlign:"center"}}>{item.strMeal}</Card.Title>
                        <Card.Text>
                            {item.strInstructions}
                        </Card.Text>
                    </Card.Body>
                    
                        
                </Card>))}
                <Row className="mb-2" style={{width:"400px", marginLeft:"30%"}}>
                        <Button as={Col} style={{float:"left", margin:"50px"}} variant="primary" onClick={()=>{
                            this.setState({
                                    error: null,
                                    isLoaded: false,
                                    items: []
                            });
                            this.componentDidMount();
                        }}>Skip</Button>
                        <Button as={Col} style={{float:"right", margin:"50px"}} variant="primary" onClick={()=>{
                            let data = JSON.stringify(items);
                            localStorage.setItem(localStorage.length,data);
                            console.log(localStorage.getItem(localStorage.length-1));
                        }}>Like</Button>
                    </Row>
            </div>
            </>
        );
    }
}


}
